const burgerMenu=document.querySelector(".burger-menu"),list=document.querySelector(".menu-list"),aside=document.querySelector(".aside"),header=document.querySelector(".header");let menuIsOpened=!1;function collapseMenu(){burgerMenu.classList.toggle("change"),list.classList.toggle("slide-down"),aside.classList.toggle("slide-right"),menuIsOpened=!menuIsOpened,header.style.backgroundColor=menuIsOpened?"white":"transparent"}burgerMenu.addEventListener("click",collapseMenu);